mapper.insert:
insert into tbl_mbti (mbti_addr, mbti_x, mbti_y, member_id
			, mbti_activity, mbti_festival, mbti_tour, mbti_solo, mbti_couple
			, mbti_buddys, mbti_family, mbti_price, mbti_sd, mbti_io)
values ('jj', 22, 33, #{member_id}
			, #{mbti_activity}, #{mbti_festival}, #{mbti_tour}, #{mbti_solo}, #{mbti_couple}
			, #{mbti_buddys}, #{mbti_family}, #{mbti_price}, #{mbti_sd}, #{mbti_io})

mapper.select:
select count(*) from tbl_member_info
where member_id = #{member_name}
and member_is_naver = #{naver}

select * from tbl_member_info
where member_id = #{id}
and member_pw = #{pw}
