mapper.select:
	select DISTINCT plan_sn, member_id, party_sn, plan_name, plan_startdate, plan_enddate, 
plan_startpoint, plan_hotel, plan_cost, plan_writer, plan_starttime,plan_endtime
from tbl_plan  where party_sn = #{party_sn}

	select * from tbl_plan
where member_id = #{member_id}
order by plan_sn desc

	select * from tbl_party
where member_id = #{member_id}
order by party_sn desc

mapper.update:
	update tbl_board_picture
set picture_filepath = #{picture_filepath}
where party_sn = #{party_sn}
